import { Button, Card, Divider, Empty, Image, message, Spin, Table } from 'antd'
import type { FC } from 'react'
import { useState } from 'react'
import type { ObjInfo } from '../../define'

import ImgDiagnose1 from '@/assets/diagnose/img1.png'
import ImgDiagnose2 from '@/assets/diagnose/img2.png'
import ImgDiagnose3 from '@/assets/diagnose/img3.png'
import ImgDiagnose4 from '@/assets/diagnose/img4.png'
import ImgDiagnose5 from '@/assets/diagnose/img5.png'

interface IProps {
  data: ObjInfo
}

const DiagnosePanel: FC<IProps> = ({ data: objData }) => {
  const columns = [
    {
      title: '编号',
      dataIndex: 'key',
      align: 'center'
    },
    {
      title: '距离',
      dataIndex: 'distance',
      align: 'center'
    },
    {
      title: '直径',
      dataIndex: 'diameter',
      align: 'center'
    },
    {
      title: '概率',
      dataIndex: 'p',
      align: 'center',
      render: (p: string) => {
        return <span className="text-[#CAAB3A]">{p}</span>
      }
    }
  ]

  const [readyToDiagnoseImgList, setReadyToDiagnoseImgList] = useState<string[]>([])
  const [diagnoseResultImgList, setDiagnoseResultImgList] = useState<string[]>([])

  const [isLoading, setIsLoading] = useState(false)

  const data = []
  const handleDiagnose = () => {
    setIsLoading(true)
    setDiagnoseResultImgList([ImgDiagnose4, ImgDiagnose5])
    setTimeout(() => {
      setIsLoading(false)
    }, 5000)
  }

  const handleImport = async () => {
    const path = await window.openFileDialog()
    if (path) {
      message.success('导入成功')
      setDiagnoseResultImgList([])
      setReadyToDiagnoseImgList([ImgDiagnose1, ImgDiagnose2, ImgDiagnose3])
    } else {
      message.warn('取消导入')
    }
  }

  for (let i = 0; i < 10; i++) {
    data.push({
      key: i + 1,
      distance: '113·146',
      diameter: Math.floor(Math.random() * 10 + 1),
      p: Math.random() > 0.5 ? '73%' : '13%'
    })
  }
  return (
    <div className="flex">
      <div className="space-y-2">
        <Card title="就诊者信息">
          <p>姓名：{objData.name}</p>
          <p>性别：{objData.sex}</p>
          <p>年龄：{objData.age}</p>
          <p>身份证号：{objData.idCard}</p>
          <p>联系方式：{objData.phone}</p>
          <p>就诊时间：{objData.date.format('yyyy-MM-DD hh:mm:ss')}</p>
        </Card>
        <Card
          title={
            <div className="flex justify-between">
              <h1>MRI扫描文件</h1>
              {readyToDiagnoseImgList.length > 0 && (
                <Button size="small" disabled={isLoading} onClick={() => handleImport()}>
                  重新导入文件
                </Button>
              )}
            </div>
          }>
          <div className="flex flex-col space-y-3 pb-3">
            {readyToDiagnoseImgList.length === 0 ? (
              <Empty description={false} />
            ) : (
              readyToDiagnoseImgList.map((img, index) => (
                <div className="flex justify-center w-[250px] h-[140px] bg-black" key={index}>
                  <Image
                    height={140}
                    src={img}
                    preview={false}
                    fallback="data:image/png;base64,"
                  />
                </div>
              ))
            )}
          </div>
          {readyToDiagnoseImgList.length > 0 ? (
            <Button
              block
              type="primary"
              size="large"
              className="!bg-[#B683CE]"
              onClick={handleDiagnose}
              disabled={isLoading}>
              开始诊断
            </Button>
          ) : (
            <>
              <div className="h-[30px]"></div>
              <Button
                block
                type="primary"
                size="large"
                className="!bg-[#B683CE]"
                onClick={() => handleImport()}
                disabled={isLoading}>
                导入MRI扫描文件
              </Button>
            </>
          )}
        </Card>
      </div>
      <div className="flex-1">
        <Card title="病灶分析">
          {isLoading && (
            <div className="w-full h-[500px] flex justify-center pt-[200px]">
              <Spin size="large" tip={'诊断中，请稍后'} />
            </div>
          )}
          {!isLoading &&
            (diagnoseResultImgList.length > 0 ? (
              <div className="flex">
                <div className="w-[465px] flex flex-col justify-center items-start  space-y-3">
                  {diagnoseResultImgList.map((img, index) => (
                    <div className="flex justify-center w-[420px] h-[443px] bg-black" key={index}>
                      <Image
                        height={443}
                        src={img}
                        preview={false}
                        fallback="data:image/png;base64,"
                      />
                    </div>
                  ))}
                </div>
                <div>
                  {/* @ts-ignore */}
                  <Table bordered columns={columns} dataSource={data} size="small"></Table>
                  <Divider />
                  <section className="flex items-center p-10">
                    <section className="text-[20px] mr-10">
                      <p>脑组织失活程度：30%</p>
                      <p>病灶比例：10%</p>
                      <p>严重程度：较严重</p>
                    </section>
                  </section>
                  <section className="w-[150px] flex flex-col space-y-3 float-right">
                    <Button className="!bg-[#D7CF25] !text-white">保存诊断记录</Button>
                    <Button className="!bg-[#51AEC6] !text-white">导出诊断记录</Button>
                  </section>
                </div>
              </div>
            ) : (
              <Empty
                description={'请先导入MRI扫描文件再进行诊断'}
                className="w-full h-[500px] pt-[200px]"
              />
            ))}
        </Card>
      </div>
    </div>
  )
}

export default DiagnosePanel
